    /**
     * Transforms a ResultSet iterating on the fl_store table on a FlStoreBean bean according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return bean resulting FlStoreBean bean
     * @throws DAOException
     */
    //30
    public FlStoreBean decodeRow(ResultSet rs, int[] fieldList,FlStoreBean bean) throws DAOException
    {
        if(null==bean)
            bean = this.createBean();
        int pos = 0;
        try
        {
            for(int i = 0; i < fieldList.length; i++)
            {
                switch(fieldList[i])
                {
                    case FL_STORE_ID_MD5:
                        ++pos;
                        bean.setMd5(rs.getString(pos));
                        break;
                    case FL_STORE_ID_ENCODING:
                        ++pos;
                        bean.setEncoding(rs.getString(pos));
                        break;
                    case FL_STORE_ID_DATA:
                        ++pos;
                        bean.setData(rs.getBytes(pos));
                        break;
                    default:
                        throw new DAOException("Unknown field id " + fieldList[i]);
                }
            }
        }
        catch(SQLException e)
        {
            throw new DataAccessException(e);
        }
        bean.isNew(false);
        bean.resetIsModified();

        return bean;
    }

