    class ListenerContainer implements TableListener<FlStoreBean> {
        private final Set<TableListener<FlStoreBean>> listeners = new TreeSet<TableListener<FlStoreBean>>();
        public ListenerContainer() {
        }
    
        @Override
        public void beforeInsert(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.beforeInsert(bean);
            }
        }
    
        @Override
        public void afterInsert(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.afterInsert(bean);
            }
        }
    
        @Override
        public void beforeUpdate(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.beforeUpdate(bean);
            }
        }
    
        @Override
        public void afterUpdate(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.afterUpdate(bean);
            }
        }
    
        @Override
        public void beforeDelete(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.beforeDelete(bean);
            }
        }
    
        @Override
        public void afterDelete(FlStoreBean bean) throws DAOException {
            for(TableListener<FlStoreBean> listener:listeners){
                listener.afterDelete(bean);
            }
        }
    
        public boolean isEmpty() {
            return listeners.isEmpty();
        }
    
        public boolean contains(TableListener<FlStoreBean> o) {
            return listeners.contains(o);
        }
    
        public synchronized boolean add(TableListener<FlStoreBean> e) {
            if(null == e)
                throw new NullPointerException();
            return listeners.add(e);
        }
    
        public synchronized boolean remove(TableListener<FlStoreBean> o) {
            return null == o? false : listeners.remove(o);
        }
    
        public synchronized void clear() {
            listeners.clear();
        }    
    }
    private final ListenerContainer listenerContainer = new ListenerContainer();

    //35
    @Override
    public void registerListener(TableListener<FlStoreBean> listener)
    {
        this.listenerContainer.add(listener);
    }

    /**
     * remove listener.
     */
    //36
    @Override
    public void unregisterListener(TableListener<FlStoreBean> listener)
    {
        this.listenerContainer.remove(listener);
    }

